/*
You are given an m x n integer matrix grid where each cell is either 0 (empty) or 1 (obstacle). 
You can move up, down, left, or right from and to an empty cell in one step.

Return the minimum number of steps to walk from the upper left corner (0, 0) to the lower right corner (m - 1, n - 1) 
given that you can eliminate at most k obstacles. 
If it is not possible to find such walk return -1.
*/
#include <iostream>
#include <vector>
#include <utility>
#include <queue>
using namespace std;

class Solution {
public:
    int shortestPath(vector<vector<int>>& grid, int k) {
        if(grid.size()==1 && grid[0].size()==1) return 0;
        vector<vector<int>> mem(grid.size(), vector<int>(grid[0].size(), -1));
        int x=0, y=0, dis=0;
        mem[0][0] = k;
        queue<pair<int, int>> q;
        q.push({x, y});
        while(!q.empty()){
            int size = q.size();
            dis++;
            for(int i=0;i<size;i++){
                pair<int, int> pos = q.front();
                q.pop();
                int kLeft = mem[pos.first][pos.second];                
                for(vector<int> dir : dirs){
                    int xp = pos.first+dir[0];
                    int yp = pos.second+dir[1];
                    if(xp==grid.size()-1 && yp==grid[0].size()-1) return dis;
                    if(xp>=0 && xp<grid.size() && yp>=0 && yp<grid[0].size() && mem[xp][yp]==-1){
                        if(grid[xp][yp]==0){
                            mem[xp][yp] = kLeft;
                            q.push({xp, yp});
                        }
                        else{
                            if(kLeft-1>=0){
                                mem[xp][yp] = kLeft-1;
                                q.push({xp, yp});
                            }
                        }
                    }
                }
            }
        }
        return -1;
    }
private:
    const vector<vector<int>> dirs = {{1,0},{0,1},{0,-1},{-1,0}};
};

