/*
We have n cities and m bi-directional roads where roads[i] = [ai, bi] connects city ai with city bi. 
Each city has a name consisting of exactly three upper-case English letters given in the string array names. 
Starting at any city x, you can reach any city y where y != x (i.e., the cities and the roads are forming an undirected connected graph).

You will be given a string array targetPath. 
You should find a path in the graph of the same length and with the minimum edit distance to targetPath.

You need to return the order of the nodes in the path with the minimum edit distance. 
The path should be of the same length of targetPath and should be valid (i.e., there should be a direct road between ans[i] and ans[i + 1]). 
If there are multiple answers return any one of them.

The edit distance is defined as follows:

define editDistance(targetPath, myPath) {
    dis := 0
    a := targetPath.length
    b := myPath.length
    if a != b {
        return 1000000000
    }
    for (i := 0; i < a; i += 1) {
        if targetPath[i] != myPath[i] {
            dis += 1
        }
    }
}

*/
#include <iostream>
#include "Debug\VectorDebug.h"
using namespace std;

class Solution {
public:
    vector<int> mostSimilar(int n, vector<vector<int>>& roads, vector<string>& names, vector<string>& targetPath) {
        ajList = vector<vector<int>>(n);
        for(vector<int> road : roads){
            ajList[road[0]].push_back(road[1]);
            ajList[road[1]].push_back(road[0]);
        }
        vector<vector<int>> mem(n, vector<int>(targetPath.size(), -1));
        int minDiff = INT_MAX;
        int startNode = -1;
        for(int i=0;i<n;i++){
            int tempDiff = dp(names, targetPath, i, 0, mem);
            if(tempDiff<minDiff){
                minDiff = tempDiff;
                startNode = i;
            }
        }
        vector<int> res;
        res.push_back(startNode);
        dfs(names, targetPath, res, mem);
        return res;
    }
private:
    vector<vector<int>> ajList;
    int dp(vector<string>& names, vector<string>& targetPath, int node, int index, vector<vector<int>>& mem){
        if(index==targetPath.size()) return 0;
        if(mem[node][index]!=-1) return mem[node][index];

        int res = INT_MAX;
        for(int neighbor : ajList[node]){
            res = min(res, dp(names, targetPath, neighbor, index+1, mem));
        }
        if(names[node]!=targetPath[index]) res++;
        return mem[node][index] = res;
    }
    bool dfs(vector<string>& names, vector<string>& targetPath, vector<int>& path, vector<vector<int>>& mem){        
        if(path.size()==targetPath.size()) return true;
        
        int node = path.back();
        int index = path.size();
        for(int neighbor : ajList[node]){
            if( (names[node]==targetPath[index-1] && mem[neighbor][index]==mem[node][index-1])
             || (names[node]!=targetPath[index-1] && mem[neighbor][index]<mem[node][index-1])){
                path.push_back(neighbor);
                if(dfs(names, targetPath, path, mem)) return true;
                path.pop_back();
            }
        }
        return false;
    }
};

