'''
You are given an m x n integer matrix grid where each cell is either 0 (empty) or 1 (obstacle). 
You can move up, down, left, or right from and to an empty cell in one step.

Return the minimum number of steps to walk from the upper left corner (0, 0) to the lower right corner (m - 1, n - 1) 
given that you can eliminate at most k obstacles. 
If it is not possible to find such walk return -1.
'''

from typing import List

class Solution:
    def shortestPath(self, grid: List[List[int]], k: int) -> int:
        rows, cols = len(grid), len(grid[0])
        seen = {(0, 0, k)}
        dirs = [[1, 0], [0, 1], [-1, 0], [0, -1]]
        q = [[0, 0, k]]
        count = 0
        while q:
            for _ in range(len(q)):
                x, y, k = q.pop(0)
                if x==rows-1 and y==cols-1:
                    return count
                for dir in dirs:
                    new_x, new_y = x+dir[0], y+dir[1]
                    if new_x>=0 and new_x<rows and new_y>=0 and new_y<cols:
                        if grid[new_x][new_y] == 0 and (new_x, new_y, k) not in seen:
                            seen.add((new_x, new_y, k))
                            q.append([new_x, new_y, k])
                        elif grid[new_x][new_y] == 1 and k>0 and (new_x, new_y, k-1) not in seen:
                            seen.add((new_x, new_y, k-1))
                            q.append([new_x, new_y, k-1])
            count+=1

        return -1

